### 5.4 AI 换衣服务

```typescript
// src/services/tryOnService.ts
import { useMutation } from '@tanstack/react-query';
import { useAppStore } from '@/stores/appStore';

const SUPABASE_FUNCTION_URL = import.meta.env.VITE_SUPABASE_URL + '/functions/v1/try-on';

interface TryOnInput {
  personImage: string; // Base64
  clothingImageUrl: string;
}

interface TryOnResult {
  imageUrl: string;
}

async function fileToBase64(file: File): Promise<string> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result as string);
    reader.onerror = reject;
  });
}

async function callTryOnAPI(input: TryOnInput): Promise<TryOnResult> {
  const response = await fetch(SUPABASE_FUNCTION_URL, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      person_image: input.personImage,
      clothing_image: input.clothingImageUrl,
    }),
  });

  if (!response.ok) {
    throw new Error('换衣处理失败');
  }

  const data = await response.json();

  // 轮询等待结果 (Replicate 异步处理)
  let result = data;
  while (result.status === 'processing' || result.status === 'starting') {
    await new Promise(resolve => setTimeout(resolve, 2000));
    const pollResponse = await fetch(result.urls.get);
    result = await pollResponse.json();
  }

  if (result.status === 'succeeded') {
    return { imageUrl: result.output[0] };
  }

  throw new Error('处理失败');
}

export function useTryOn() {
  const { setIsProcessing, setResultImageUrl, setProcessingProgress } = useAppStore();

  return useMutation({
    mutationFn: async ({ personImage, clothingImageUrl }: { 
      personImage: File; 
      clothingImageUrl: string 
    }) => {
      setIsProcessing(true);
      setProcessingProgress(10);

      const base64Image = await fileToBase64(personImage);
      setProcessingProgress(30);

      const result = await callTryOnAPI({
        personImage: base64Image,
        clothingImageUrl,
      });

      setProcessingProgress(100);
      return result;
    },
    onSuccess: (data) => {
      setResultImageUrl(data.imageUrl);
      setIsProcessing(false);
    },
    onError: () => {
      setIsProcessing(false);
    },
  });
}
